<?phpnamespace MisterTango\Payment\Setup;/** * Class InstallSchema * @package MisterTango\Payment\Setup */class InstallSchema implements \Magento\Framework\Setup\InstallSchemaInterface{    /**     * @param \Magento\Framework\Setup\SchemaSetupInterface $setup     * @param \Magento\Framework\Setup\ModuleContextInterface $context     */    public function install(        \Magento\Framework\Setup\SchemaSetupInterface $setup,        \Magento\Framework\Setup\ModuleContextInterface $context    ) {        $installer = $setup;        $installer->startSetup();        $table = $installer->getConnection()            ->newTable($installer->getTable('transactions_mistertango'))            ->addColumn(                'transaction_id',                \Magento\Framework\DB\Ddl\Table::TYPE_INTEGER,                null,                ['identity' => true, 'nullable' => false, 'primary' => true],                'ID'            )            ->addColumn(                'amount',                \Magento\Framework\DB\Ddl\Table::TYPE_DECIMAL,                '10,2',                [],                'Amount'            )            ->addColumn(                'order_id',                \Magento\Framework\DB\Ddl\Table::TYPE_TEXT,                null,                [],                'Order Id'            )            ->addColumn(                'websocket',                \Magento\Framework\DB\Ddl\Table::TYPE_TEXT,                null,                [],                'Websocket'            )            ->addColumn(                'payment_date',                \Magento\Framework\DB\Ddl\Table::TYPE_TIMESTAMP,                null,                ['nullable' => false, 'default' => \Magento\Framework\DB\Ddl\Table::TIMESTAMP_INIT_UPDATE],                'Payment Date'            )            ->setComment('Transactions MisterTango');        $table2 = $installer->getConnection()            ->newTable($installer->getTable('callbacks_mistertango'))            ->addColumn(                'callback_uuid',                \Magento\Framework\DB\Ddl\Table::TYPE_INTEGER,                null,                ['identity' => true, 'nullable' => false, 'primary' => true],                'Callback Uuid'            )            ->addColumn(                'transaction_id',                \Magento\Framework\DB\Ddl\Table::TYPE_TEXT,                null,                [],                'Transaction Id'            )            ->addColumn(                'amount',                \Magento\Framework\DB\Ddl\Table::TYPE_DECIMAL,                '10,2',                [],                'Amount'            )            ->addColumn(                'callback_date',                \Magento\Framework\DB\Ddl\Table::TYPE_TIMESTAMP,                null,                ['nullable' => false, 'default' => \Magento\Framework\DB\Ddl\Table::TIMESTAMP_INIT_UPDATE],                'Callback Date'            )            ->setComment('Callbacks MisterTango');        $installer->getConnection()->createTable($table);        $installer->getConnection()->createTable($table2);        $installer->endSetup();    }}